// src/App.js

import React from 'react';
import Chatbot from './Chatbot'; // Import the Chatbot component
import './Chatbot.css'; // Import the CSS file for styles

function App() {
    return (
        <div className="BOT">
            <header>
                <div className="logo"></div>
                <h1 className="tagline">Bharat BOT</h1>
            </header>
            <div className="hero">
                <h1>Welcome to Bharat BOT</h1>
                <h2>Your guide to Indian culture and heritage</h2>
                <button className="cta-button">Get Started</button>
            </div>
            <Chatbot />
        </div>
    );
}

export default App;
