// src/Chatbot.js

import React, { useState } from 'react';
import './Chatbot.css'; // Ensure you have your CSS file for styling

const Chatbot = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);

    const handleSend = () => {
        if (input.trim()) {
            const userMessage = { text: input, sender: 'user' };
            setMessages([...messages, userMessage]);
            setInput('');
            simulateBotResponse(input);
        }
    };

    const simulateBotResponse = (userInput) => {
        setIsTyping(true);
        setTimeout(() => {
            const botResponse = { text: "You said: " + userInput, sender: 'bot' };
            setMessages((prevMessages) => [...prevMessages, botResponse]);
            setIsTyping(false);
        }, 1000); // Simulates a delay for the bot response
    };

    const quickReply = (reply) => {
        const userMessage = { text: reply, sender: 'user' };
        setMessages([...messages, userMessage]);
        simulateBotResponse(reply);
    };

    return (
        <div className="chatbot-container">
            <div className="chat-window">
                <div className="messages">
                    {messages.map((msg, index) => (
                        <div key={index} className={msg.sender}>
                            {msg.text}
                        </div>
                    ))}
                    {isTyping && <div className="bot typing">Bot is typing...</div>}
                </div>
                <div className="input-area">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && handleSend()}
                        placeholder="Type a message..."
                    />
                    <button onClick={handleSend}>Send</button>
                </div>
                <div className="quick-replies">
                    <button onClick={() => quickReply("Get a Ticket")}>Get a Ticket</button>
                    <button onClick={() => quickReply("Quick Guide")}>Quick Guide</button>
                    <button onClick={() => quickReply("Ab kya lauda lega isme")}>Ab kya lauda lega isme</button>
                </div>
            </div>
        </div>
    );
};

export default Chatbot;
