import React, { useState, useEffect } from 'react';
import './SearchBar.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const DropdownWithSearchBar = ({ onSearch }) => {
    const [selectedOption, setSelectedOption] = useState('');
    const [options, setOptions] = useState([]);
    const [tabledata, settabledata] = useState([]);
    const [showSearchBar, setShowSearchBar] = useState(true); // Toggle visibility

    useEffect(() => {
        fetch('http://localhost:5000/state-data')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setOptions(data);
            })
            .catch((error) => {
                console.error('Error fetching state data:', error);
                alert('Something went wrong while fetching state data!');
            });
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/get-museum-data', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ state: selectedOption }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            settabledata(data);
            setSelectedOption('');
        } catch (error) {
            console.error('Error fetching museum data:', error);
            alert('Something went wrong while fetching museum data!');
        }
    };

    return (
        <div className="container mt-4">
            {/* Toggle Buttons */}
            {showSearchBar ? (
                <button
                    className="btn btn-danger mb-3"
                    onClick={() => setShowSearchBar(false)}
                >
                    Close Search Bar
                </button>
            ) : (
                <button
                    className="btn btn-primary mb-3"
                    onClick={() => setShowSearchBar(true)}
                >
                    Show Search Bar
                </button>
            )}

            {/* Search Bar */}
            {showSearchBar && (
                <form onSubmit={handleSubmit}>
                    <select
                        value={selectedOption}
                        onChange={(e) => setSelectedOption(e.target.value)}
                        className="search-input"
                    >
                        <option value="" disabled>Select the state</option>
                        {options.map((option, index) => (
                            <option key={index} value={option}>
                                {option}
                            </option>
                        ))}
                    </select>

                    <button type="submit" className="search-button">Explore</button>
                </form>
            )}

            {/* Table Display */}
            {tabledata.length > 0 ? (
                <>
                    <h2 className="mt-4">Bootstrap Table in React</h2>
                    <table className="table table-striped table-bordered">
                        <thead className="thead-dark">
                            <tr>
                                <th>State</th>
                                <th>Name of Museum</th>
                                <th>Name of District</th>
                                <th>Location</th>
                                <th>Opening Time</th>
                                <th>Closing Time</th>
                                <th>Ticket Price</th>
                                <th>Closing Days</th>
                                <th>Book Ticket</th>
                            </tr>
                        </thead>
                        <tbody>
                            {tabledata.map((row) => (
                                <tr key={row._id}>
                                    <td>{row.state}</td>
                                    <td>{row.nameofmuseum}</td>
                                    <td>{row.nameofdistrict}</td>
                                    <td>{row.location}</td>
                                    <td>{row.openingtime}</td>
                                    <td>{row.closingtime}</td>
                                    <td>{row.ticketprice}</td>
                                    <td>{row.closingdays}</td>
                                    <td>
                                        <button
                                            type="button"
                                            onClick={() => window.open('/form.html', '_blank')}
                                            className="btn btn-success btn-sm"
                                        >
                                            Book Ticket
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            ) : (
                <p className="mt-4">Welcome to Amanâ€™s museum ticket booking site!</p>
            )}
        </div>
    );
};

export default DropdownWithSearchBar;
