// src/ChatIcon.js

import React, { useState } from 'react';
import Chatbot from './Chatbot'; // Import the Chatbot component
import './Chatbot.css'; // Ensure styles are applied
import newLogo from './"C:\Users\HP\chatbot-interface\public\Chatbot icon.jpeg"';

export const ChatbotLogo = () => (
    <img src={"C:\Users\HP\chatbot-interface\public\Chatbot icon.jpeg"} alt="Chatbot Logo" className="chatbot-logo" />
);

const ChatIcon = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleChatbox = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div>
            <div className="chat-icon" onClick={toggleChatbox}>
                {/* Chat bubble design */}
                <div className="chat-bubble">
                    <span role="img" aria-label="chat"></span>
                </div>
            </div>
            {isOpen && <Chatbot />}
        </div>
    );
};

export default ChatIcon;
