import React, { useState } from 'react';
import './SearchBar.css';

const DropdownWithSearchBar = ({ onSearch }) => {
    const [query, setQuery] = useState('');
    const [selectedOption, setSelectedOption] = useState('');

    const options = [
         'Allahabad Museum','Bundelkhand Museum', 'Abai Memorial Panchal Museum','Rahul Museum',
        'Government Bauddh Museum','Rani Mahal Museum','Kanpur Sangrahalaya','Bauddh Museum', 'Bal Museum',
        'Provincial Hygiene Institute','State Museum','Lok Kala Museum', 'Mathura Museum','State Jain museum Museum',
        'District Museum','Sarnath Museum','Bharat Kala Bhawan Museum','Ramnagar museum'
    ];

    const handleSearch = () => {
        onSearch(query || selectedOption); // Use selected option if query is empty
        setQuery(''); // Clear the input after search
    };

    const handleSelectChange = (event) => {
        setSelectedOption(event.target.value);
    };

    return (
        <div className="search-container">
            
            <select value={selectedOption} onChange={handleSelectChange}>
                <option value="" disabled>Select</option>
                {options.map((option, index) => (
                    <option key={index} value={option}>
                        {option}
                    </option>
                ))}
            </select>
            
            <input
                type="text"
                className="search-input"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Search here what you want to explore...."
            />
            <button className="search-button" onClick={handleSearch}>
                Explore
            </button>
        </div>
    );
};

export default DropdownWithSearchBar;
