import React, { useState, useEffect } from 'react';
import './SearchBar.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const DropdownWithSearchBar = ({ onSearch }) => {
    const [selectedOption, setSelectedOption] = useState('');
    const [options, setOptions] = useState([]);
    const [tabledata, settabledata] = useState([]);
    const [showTable, setShowTable] = useState(false); // New state to toggle table

    useEffect(() => {
        fetch('http://localhost:5000/state-data')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setOptions(data);
            })
            .catch((error) => {
                console.error('Error fetching state data:', error);
                alert('Something went wrong while fetching state data!');
            });
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();
        settabledata([]); // Clear previous table data
        setShowTable(false); // Hide table before new data loads

        try {
            const response = await fetch('http://localhost:5000/get-museum-data', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ state: selectedOption }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            settabledata(data);
            setShowTable(true); // Show table after data is loaded
            setSelectedOption('');
        } catch (error) {
            console.error('Error fetching museum data:', error);
            alert('Something went wrong while fetching museum data!');
        }
    };

    return (
        <div className="container mt-4">
            <form onSubmit={handleSubmit}>
                <select
                    value={selectedOption}
                    onChange={(e) => setSelectedOption(e.target.value)}
                    className="search-input"
                >
                    <option value="" disabled>Select the state</option>
                    {options.map((option, index) => (
                        <option key={index} value={option}>
                            {option}
                        </option>
                    ))}
                </select>

                <button type="submit" className="search-button">Explore</button>
            </form>

            {/* Toggle Table Visibility */}
            {showTable && (
                <button
                    className="btn btn-warning mt-3"
                    onClick={() => setShowTable(false)}
                >
                    Close Table
                </button>
            )}

            {/* Table Display */}
            {showTable && tabledata.length > 0 && (
                <>
                    <h2 className="mt-4">Bootstrap Table in React</h2>
                    <table className="table table-striped table-bordered">
                        <thead className="thead-dark">
                            <tr>
                                <th>State</th>
                                <th>Name of Museum</th>
                                <th>Name of District</th>
                                <th>Location</th>
                                <th>Opening Time</th>
                                <th>Closing Time</th>
                                <th>Ticket Price</th>
                                <th>Closing Days</th>
                                <th>Book Ticket</th>
                            </tr>
                        </thead>
                        <tbody>
                            {tabledata.map((row) => (
                                <tr key={row._id}>
                                    <td>{row.state}</td>
                                    <td>{row.nameofmuseum}</td>
                                    <td>{row.nameofdistrict}</td>
                                    <td>{row.location}</td>
                                    <td>{row.openingtime}</td>
                                    <td>{row.closingtime}</td>
                                    <td>{row.ticketprice}</td>
                                    <td>{row.closingdays}</td>
                                    <td>
                                        <button
                                            type="button"
                                            onClick={() => window.open('/form.html', '_blank')}
                                            className="btn btn-success btn-sm"
                                        >
                                            Book Ticket
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            )}

            {/* Fallback Message */}
            {!showTable && tabledata.length === 0 && (
                <p className="mt-4">Welcome to Amanâ€™s museum ticket booking site!</p>
            )}
        </div>
    );
};

export default DropdownWithSearchBar;
