import React from 'react';
import DropdownWithSearchBar from './DropdownWithSearchBar';
import ChatIcon from './ChatIcon'; // Import the ChatIcon component
//import Collage from './Collage'; // Import the Collage component
import './App.css';

function App() {
    const handleSearch = (query) => {
        console.log('Searching for:', query); // Handle search logic here
    };

    return (
        <div className="animated-bg"> {/* Applying the animated background */}
         <header>
                <a href="/" className="logo-link">
                    <img src={`${process.env.PUBLIC_URL}/background98.png`} alt="Website Logo" className="logo" />
                </a>
                <h1 className="title">Journey Through India's Cultural Heritage</h1>
            </header>

            <div className="BOT">
                <DropdownWithSearchBar onSearch={handleSearch} />
                <ChatIcon />
            </div>
        </div>
    );
}

const sites = [
    {
        name: 'Taj Mahal',
        image: './tajmahal.jpeg',
        url: 'https://en.wikipedia.org/wiki/Taj_Mahal'
    },
    {
        name: 'Sarnath Museum',
        image: './sarnath.webp',
        url: 'https://en.wikipedia.org/wiki/Sarnath_Museum'
    },
    {
        name: 'Redfort',
        image: './red fort.webp',
        url: 'https://en.wikipedia.org/wiki/Red_Fort'
    },
    {
        name: 'Allahabad Museum',
        image: './allahabadm.jpg',
        url: 'https://en.wikipedia.org/wiki/Allahabad_Museum'
    },
    {
        name: "Delhi illusion museum",
        image: './delhim.webp',
        url: 'https://museumofillusions.in/'
    },
    {
        name: "Chhatrapati Shivaji MVS",
        image: './CSMVS.jpeg',
        url: 'https://en.wikipedia.org/wiki/Chhatrapati_Shivaji_Maharaj_Vastu_Sangrahalaya'
    },
    {
        name:"Hampi",
        image:'hampi.jpeg',
        url:'https://en.wikipedia.org/wiki/Hampi'
    },
    {
        name:"Mahabalipuram",
        image:'maha.jpeg',
        url:'https://en.wikipedia.org/wiki/Mamallapuram'
    }


    // Add more sites as needed
];

const Collage = () => {
    return (
        <div className="collage-container">
            {sites.map((site, index) => (
                <a href={site.url} key={index} className="collage-item">
                    <img src={site.image} alt={site.name} className="collage-image" />
                    <div className="collage-title">{site.name}</div>
                </a>
            ))}
        </div>
    );
};



export default App;
