import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';

const responses = {
    // Your predefined responses
    "Get a Ticket": "Here’s the link to get your ticket: [Ticket Link]",
    "Quick Guide": "You can find the quick guide here: [Guide Link]",
    "Hello": "Hi there! How can I help you today?",
    "Help": "Sure! What do you need help with?",
    "Bye": "Goodbye! Have a great day!",
    "Museum Timings": "The museum is open from 10 AM to 6 PM, every day.",
    "Special Exhibits": "Currently, we have exhibits on Indian Art and History. Would you like to know more?",
    "Cultural Heritage": "India has a rich cultural heritage, including classical dance forms, music, and art. Do you want to learn about a specific aspect?",
    "Famous Monuments": "India is home to many famous monuments like the Taj Mahal, Qutub Minar, and Hampi. Would you like details on any of these?",
    "Events This Month": "This month, we have a special exhibition on Mughal Architecture. It runs from the 15th to the 30th. Would you like to book a ticket?",
};

const buttonSets = {
    default: ["Get a Ticket", "Museum Timings", "Special Exhibits", "Cultural Heritage", "Famous Monuments", "Events This Month", "Quick Guide", "Hello", "Help", "Bye"],
    "Get a Ticket": ["Ticket Prices", "Booking Process", "Cancel Ticket"],
    "Museum Timings": ["Weekdays", "Weekends", "Holidays"],
    "Special Exhibits": ["Indian Art", "History", "Return to Main Menu"],
    "Cultural Heritage": ["Dance Forms", "Music", "Art"],
    "Famous Monuments": ["Taj Mahal", "Qutub Minar", "Hampi"],
    "Events This Month": ["Mughal Architecture", "Dance Show", "Return to Main Menu"],
    "Quick Guide": ["Exhibit Map", "Visitor Tips", "Return to Main Menu"],
    "Hello": ["How can I help you?", "Tell me more", "Return to Main Menu"],
    "Help": ["Technical Support", "FAQ", "Return to Main Menu"],
    "Bye": ["Goodbye", "See you soon", "Return to Main Menu"],
};

const Chatbot = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const [currentButtons, setCurrentButtons] = useState('default'); // State to track current button set
    const messagesEndRef = useRef(null); // Create a ref for the messages container

    const handleSend = () => {
        if (input.trim()) {
            const userMessage = { text: input, sender: 'user' };
            setMessages((prevMessages) => [...prevMessages, userMessage]);
            setInput('');
            simulateBotResponse(input);
        }
    };

    const simulateBotResponse = (userInput) => {
        setIsTyping(true);
        setTimeout(() => {
            const botMessage = {
                text: getBotResponse(userInput),
                sender: 'bot',
            };
            setMessages((prevMessages) => [...prevMessages, botMessage]);
            setIsTyping(false);
            if (buttonSets[userInput]) { // Check if user input has nested buttons
                setCurrentButtons(userInput);
            }
        }, 1000);
    };

    const getBotResponse = (userInput) => {
        return responses[userInput] || "Sorry, I don't have that information. Can I help you with something else?";
    };

    const quickReply = (reply) => {
        const userMessage = { text: reply, sender: 'user' };
        setMessages((prevMessages) => [...prevMessages, userMessage]);
        simulateBotResponse(reply);
    };

    // Scroll to the bottom of the messages when new messages are added
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    // Render buttons based on current state
    const renderButtons = () => {
        return (
            buttonSets[currentButtons]?.map((buttonText) => (
                <button key={buttonText} onClick={() => quickReply(buttonText)}>{buttonText}</button>
            ))
        );
    };

    return (
        <div className="chatbot-container">
            <div className="chat-window">
                <div className="messages">
                    {messages.map((msg, index) => (
                        <div key={index} className={msg.sender}>
                            {msg.text}
                        </div>
                    ))}
                    {isTyping && <div className="bot typing">Bot is typing...</div>}
                    <div ref={messagesEndRef} /> {/* This is the end ref */}
                </div>
                <div className="quick-replies">
                    {renderButtons()}
                </div>
            </div>
        </div>
    );
};

export default Chatbot;
