import React, { useState } from 'react';
import './Chatbot.css'; // Import your CSS file

function App() {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    const handleSend = () => {
        if (input.trim()) {
            // Add user message
            setMessages([...messages, { text: input, type: 'user' }]);
            setInput('');

            // Simulate bot response
            setTimeout(() => {
                setMessages(prevMessages => [
                    ...prevMessages,
                    { text: "I'm a bot! How can I assist you?", type: 'bot' }
                ]);
            }, 1000);
        }
    };

    return (
        <div>
            {/* Header */}
            <header>
                <h1>Chatbot</h1>
            </header>

            {/* Hero Section */}
            <div className="hero">
                <h1>Welcome to the Chatbot</h1>
                <h2>Your assistant is here!</h2>
                <button className="cta-button">Get Started</button>
            </div>

            {/* Chatbot Container */}
            <div className="chatbot-container">
                <div className="chat-window">
                    <div className="messages">
                        {messages.map((msg, index) => (
                            <div key={index} className={msg.type}>
                                {msg.text}
                            </div>
                        ))}
                    </div>
                    <div className="quick-replies">
                        <input
                            type="text"
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            placeholder="Type a message..."
                        />
                        <button onClick={handleSend}>Send</button>
                    </div>
                </div>
            </div>

            {/* Footer */}
            <footer>
                <div className="footer-contact">Contact Us</div>
                <div className="footer-socials">
                    <a href="#!">Facebook</a>
                    <a href="#!">Twitter</a>
                    <a href="#!">Instagram</a>
                </div>
                <div className="footer-copyright">Â© 2024 Chatbot Inc.</div>
            </footer>
        </div>
    );
}

export default App;
