import React, { useState, useEffect } from 'react';
import './SearchBar.css';
import 'bootstrap/dist/css/bootstrap.min.css';


const DropdownWithSearchBar = ({ onSearch }) => {
    const [query, setQuery] = useState('');
    const [selectedOption, setSelectedOption] = useState('');
    const [options, setOptions] = useState([]);

    const data = [
        { id: 1, name: 'John Doe', email: 'john@example.com', age: 25 },
        { id: 2, name: 'Jane Smith', email: 'jane@example.com', age: 30 },
        { id: 3, name: 'Bob Johnson', email: 'bob@example.com', age: 35 }
    ];


    useEffect(() => {
        fetch('http://localhost:5000/state-data')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setOptions(data);    // Set the data to state
            });
        // .catch((error) => {
        //     alert('something went wrong !');
        // });
    }, []);


   



    return (
        <>
            <div className="search-container">

            <form onSubmit={handleSubmit}>
                <select  value={selectedOption} onChange={(e) => setSelectedOption(e.target.value)} className="search-input">
                    <option value="" disabled>Select</option>
                    {options.map((option, index) => (
                        <option key={index} value={option}>
                            {option}
                        </option>
                    ))}
                </select>

                {/* <input
                    type="text"
                    className="search-input"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Search here what you want to explore...."
                /> */}
                
                <button type="submit"  className="search-button">Explore</button>
            </form>
            </div>

            <div className="container mt-4">
                <h2>Bootstrap Table in React</h2>
                <table className="table table-striped table-bordered">
                    <thead className="thead-dark">
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Age</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((row) => (
                            <tr key={row.id}>
                                <td>{row.id}</td>
                                <td>{row.name}</td>
                                <td>{row.email}</td>
                                <td>{row.age}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>

    );
};

export default DropdownWithSearchBar;
