import React, { useState, useEffect } from 'react';
import './SearchBar.css';
import 'bootstrap/dist/css/bootstrap.min.css';


const DropdownWithSearchBar = ({ onSearch }) => {
    const [query, setQuery] = useState('');
    const [selectedOption, setSelectedOption] = useState('');
    const [options, setOptions] = useState([]);

    const data = [
        { id: 1, name: 'John Doe', email: 'john@example.com', age: 25 },
        { id: 2, name: 'Jane Smith', email: 'jane@example.com', age: 30 },
        { id: 3, name: 'Bob Johnson', email: 'bob@example.com', age: 35 }
      ];
    
    // const options = [
    //     'Allahabad Museum', 'Bundelkhand Museum', 'Abai Memorial Panchal Museum', 'Rahul Museum',
    //     'Government Bauddh Museum', 'Rani Mahal Museum', 'Kanpur Sangrahalaya', 'Bauddh Museum', 'Bal Museum',
    //     'Provincial Hygiene Institute', 'State Museum', 'Lok Kala Museum', 'Mathura Museum', 'State Jain museum Museum',
    //     'District Museum', 'Sarnath Museum', 'Bharat Kala Bhawan Museum', 'Ramnagar museum'
    // ];
    useEffect(() => {
        // Fetch data from the API on component mount
        fetch('http://localhost:5000/state-data')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setOptions(data);    // Set the data to state
            });
            // .catch((error) => {
            //     alert('something went wrong !');
            // });
    }, []);


    const handleSearch = () => {
        onSearch(query || selectedOption); // Use selected option if query is empty
        setQuery(''); // Clear the input after search
    };

    const handleSelectChange = (event) => {
        setSelectedOption(event.target.value);
    };



    return (
        <div className="search-container">

            <select value={selectedOption} onChange={handleSelectChange} className="search-input">
                <option value="" disabled>Select</option>
                {options.map((option, index) => (
                    <option key={index} value={option}>
                        {option}
                    </option>
                ))}
            </select>

            {/* <input
                type="text"
                className="search-input"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Search here what you want to explore...."
            /> */}
            <button className="search-button" onClick={handleSearch}>
                Explore
            </button>
        </div>
    );
};

export default DropdownWithSearchBar;
